import React, { Component } from 'react'
import Teksti1 from './Teksti1';
import './jono.css';

class Jono extends Component {

  constructor(props) {
    super(props)
    this.state = { 
      date: new Date(), 
      num1: Math.floor(Math.random() * 10) + 1,
      num2: Math.floor(Math.random() * 10) + 1,
      oma: '0',
      plus1: '1'
    };
  }

  componentDidMount() {
    this.timerID = setInterval( () => this.tick(), 1000 );
    this.timerID2 = setInterval( () => this.tick2(), 500)
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  tick() {
    this.setState({
      date: new Date()
    });
  }

  // tick2() {
  //   this.setState({
  //     num1: Math.floor(Math.random() * 10) + 1
  //   })

    tick2 = () => {
      this.setState(prevState => {
        return { num1: prevState.num1 + 1 };
      });
    };

    //this.state.num1 = this.state.num1 +1;






  render() {
    return (
      <div className="outer">
        <br />
        <Teksti1 teksti="Montako jonossa" divi="teksti1" />
        <Teksti1 teksti="Seuraava numero" divi="teksti2" />
        <br /><br />
        <Teksti1 teksti={this.state.num1} divi="numero1" />
        <Teksti1 teksti={this.state.num2} divi="numero2" />
        <br /><br /><br /><br />
        <Teksti1 teksti="Oma Numero" divi="teksti2" />
        <br /><br />
        <Teksti1 teksti="1" divi="seuraava" />
        <br /><br />
        <button className="omanumero" onClick="null">Ota Numero</button>
        <br /><br />
        <button className="peruuta" onClick="null">Peruuta numero</button>
        <div><h2>{this.state.date.toLocaleTimeString()}</h2></div>
      </div>
    )
  }
}

export default Jono
