import React, { Component } from 'react'
import Teksti1 from './Teksti1';
import './jono.css';
import Random from './Random';

class Jono extends Component {

  constructor(props) {
    super(props)
    this.state = {
      timer1: '1000',
      timer2: '2000',
      plus: 1,
      plus2: 2,
      num1: '',
      num2: '',
      oma: '',
      date: new Date(),
    };
  }


  componentDidMount() {
    this.timerID = setInterval(
      () => this.tick(),
      1000
    );
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  tick() {
    this.setState({
      date: new Date(),
      num1: <Random plus={this.state.plus} timer1={this.state.timer1}/>,
      num2: <Random plus={this.state.plus} timer1={this.state.timer2}/>
    });
  }




  render() {
    return (
      <div className="outer">
        <br />
        <Teksti1 teksti="Montako jonossa" divi="teksti1" />
        <Teksti1 teksti="Seuraava numero" divi="teksti2" />
        <br /><br />
        <Teksti1 teksti={this.state.num1} divi="numero1" />

        <Teksti1 teksti={this.state.num2} divi="numero2" />
        <br /><br /><br /><br />
        <Teksti1 teksti="Oma Numero" divi="teksti2" />
        <br /><br />
        <Teksti1 teksti="" divi="seuraava" />
        <br /><br />
        <button className="omanumero" >Ota Numero</button>
        <br /><br />
        <button className="peruuta" >Peruuta numero</button>
        <Random plus={this.state.plus} timer1={this.state.timer1}/>
        <br />
        <Random plus={this.state.plus} timer1={this.state.timer2}/>
        <br /><br />
        <h1>{this.state.date.toLocaleTimeString()}</h1>
      </div>
    )
  }
}

export default Jono
