import React, { Component } from 'react'
import Teksti1 from './Teksti1';
import './jono.css';
import Random from './Random';
// import Data from './Data';

class Jono extends Component {

  constructor(props) {
    super(props)
    this.state = {
      timer1: '1000',
      timer2: '100',
      plus: 1,
      plus2: 2,
      num1: Math.floor(Math.random() * 10) + 1,
      num2: Math.floor(Math.random() * 10) + 1,
      oma: 0,
      test: 0,
      date: new Date(),
      nums: [],
    };

  }

  componentDidMount() {
    this.timerID = setInterval(() => this.tick(), this.state.timer1)
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  // tick() {
  //   this.setState({
  //     date: new Date(),
  //   //  num1: <Random plus={this.state.plus} timer1={this.state.timer1}/>,
  //   //  num2: <Random plus={this.state.plus} timer1={this.state.timer2}/>,
  //   });
  // }

  tick = () => {
    if (this.state.num1 === this.state.num2) {
      console.log(this.state.num1)
    }
    this.setState(prevState => {
        return {
            num2: prevState.num2 + this.state.plus,
            num1: prevState.num1 + Math.floor(Math.random() * 2)
        };
    });
};

  otanumero = () => {

    //console.log(this.state.oma)

     return this.setState({
          oma: this.state.num1 + this.state.num2
      //  oma: this.state.test + 1
      // nums: this.state.num1        
      //oma: parseInt(this.state.num1) + parseInt(this.state.num2)
     // oma: this.state.num2

    })
  }



  render() {
   //console.log(this.state.nums)
    return (
      <div className="outer">
        <br />
        <Teksti1 teksti="Montako jonossa" divi="teksti1" />
        <Teksti1 teksti="Seuraava numero" divi="teksti2" />
        <br /><br />
        <Teksti1 teksti={this.state.num1} divi="numero1" />
        <Teksti1 teksti={this.state.num2} divi="numero2" />
        <br /><br /><br /><br />
        <Teksti1 teksti="Oma Numero" divi="teksti2" />
        <br /><br />
        <Teksti1 teksti={this.state.oma} divi="seuraava" />
        <br /><br />
        <button className="omanumero" onClick={this.otanumero}>Ota Numero</button>
        <br /><br />
        <button className="peruuta" >Peruuta numero</button>

        <br /><br />
        <h2>{this.state.date.toLocaleTimeString()}</h2>
        <br />
      </div>
    )
  }
}

