import React, { Component } from 'react'
import Teksti1 from './Teksti1';
import './jono.css';
// import Random from './Random';
// import Data from './Data';
import ReactNotifications from 'react-browser-notifications';
import Arvio from './Arvio';
import axios from 'axios';

class Jono extends Component {

  constructor(props) {
    super(props)
    this.state = {
      timer1: '1000',
      timer2: '100',
      plus: 1,
      plus2: 2,
      num1: Math.floor(Math.random() * 10) + 1,
      num2: Math.floor(Math.random() * 10) + 1,
      oma: '',
      test: 0,
      date: new Date(),
      nums: [],
      showNotifications: this.showNotifications.bind(this),
      hideperuuta: 'none',
      arvioAika: '0',
      hideLogin: 'block',
      hideOma: 'none',
      users: [],
      store: [],
      name: '',
      id: '',
      hostname: '',
      jono: '',
      seuraava: '',
    };

  }

  showNotifications() {
    if(this.n.supported()) this.n.show();
  }


  axiosTest() {
    axios.get('http://192.168.220.139:9595/components/api/server/id/1')
      .then(res => {
        const jotain = res.data;
        this.setState({
          name: jotain.hostname,
          id: jotain.location,
        })
      })
    }


  componentDidMount() {
    this.timerID = setInterval(() => this.tick(), this.state.timer1)
    // axios.get('http://192.168.220.139:9595/components/api/server/id/1')
    // .then(response => 
    //   response.data.results.map(user => ({
    //     name: `${user.name}`,
    //     id: `${user.id}`,
    //     hostname: `${user.hostname}`,
    //   }))
    //   )



  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  // tick() {
  //   this.setState({
  //     date: new Date(),
  //   //  num1: <Random plus={this.state.plus} timer1={this.state.timer1}/>,
  //   //  num2: <Random plus={this.state.plus} timer1={this.state.timer2}/>,
  //   });
  // }

  tick = () => {
    this.axiosTest()
    if (Math.sign(this.arvioAika) < '1') 
    {
      this.arvioAika = ''
    }
    else if (this.state.oma > 0) {
      this.arvioAika = (this.state.oma - this.state.num2) * 5
    }
    else {
      this.arvioAika = (this.state.num1) * 5 
    }

    if (this.state.oma === this.state.num2) {
      this.showNotifications()
      console.log(this.num1)
      alert("123")
    }


    this.setState(prevState => {
        return {
            num2: prevState.num2 + this.state.plus,
          //  num1: prevState.num1 + Math.floor(Math.random() * 2),
            num1: this.state.id,
          //  num2: this.state.hostname
        };
    });

};

  otanumero = () => {
    //console.log(this.state.oma)

     return this.setState({
        oma: this.state.num1 + this.state.num2,
        hideperuuta: 'block',
      //  oma: this.state.test + 1
      // nums: this.state.num1        
      //oma: parseInt(this.state.num1) + parseInt(this.state.num2)
     // oma: this.state.num2
    })
  }

  peruutaNumero = () => {

    return this.setState({
      oma: '',
      hideperuuta: 'none',
    })
  }

  aikaArvio() {
    return  <Arvio time={this.arvioAika} />
  }

  kirjaudu= () => {

    return this.setState({
      hideOma: 'block',
      hideLogin: 'none',
    })
  }

  render() {
   //console.log(this.state.nums)'
   let peruutaStyle = {
      display: this.state.hideperuuta,
    }
    let loginStyle = {
      display: this.state.hideLogin,
    }
    let omaStyle = {
      display: this.state.hideOma,
    }
    return (
      <div className="outer">
        <br />
        <Teksti1 teksti="Montako jonossa" divi="teksti1" />
        <Teksti1 teksti="Seuraava numero" divi="teksti2" />
        <br /><br />
        <Teksti1 teksti={this.state.num1} divi="numero1" />
        <Teksti1 teksti={this.state.num2} divi="numero2" />
        <br />
        <div className='arvio'>{this.aikaArvio()}
        </div>
        <Teksti1 teksti='min' divi="arvio2" />
        <br /><br /><br />
        <Teksti1 teksti="Oma Numero" divi="teksti2" />
        <br /><br />
        <Teksti1 teksti={this.state.oma} divi="seuraava" />
        <br /><br />

        <button className="login" style={loginStyle} onClick={this.kirjaudu}>Kirjaudu sisään</button>
        <button className="omanumero" style={omaStyle} onClick={this.otanumero}>Ota Numero</button>
        <br /><br />
        <button className="peruuta" style={peruutaStyle} onClick={this.peruutaNumero}>Peruuta numero</button>

        <ReactNotifications
            onRef={ref => (this.n = ref)} // Required
            title="Hey There!" // Required
            body="This is the body"
            icon="icon.png"
            tag="abcdef"
            timeout="20000">
        </ReactNotifications>
        {this.state.name}
        <div>{this.state.id}</div>
      </div>

    )
  }
}

export default Jono
