import React, { Component } from 'react'
import Teksti1 from './Teksti1';
import './jono.css';
import Random from './Random';

class Jono extends Component {

  constructor(props) {
    super(props)
    this.state = {
      date: new Date(),
      num1: Math.floor(Math.random() * 10) + 1,
      num2: Math.floor(Math.random() * 10) + 1,
      timer1: '1000',
      timer2: '2000',
      plus: 1,
      plus2: 2,
    };
  }

  componentDidMount() {
    this.timerID = setInterval(() => this.tick(), 1000);
    this.timerID2 = setInterval(() => this.tick2(), 2000)
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
    clearInterval(this.timerID2)
  }

  tick() {
    this.setState({
      date: new Date()
    });
  }


  tick2 = () => {
    this.setState(prevState => {
      return {
        num1: prevState.num1 + 1
      };
    });
  };

  render() {
    return (
      <div className="outer">
        <br />
        <Teksti1 teksti="Montako jonossa" divi="teksti1" />
        <Teksti1 teksti="Seuraava numero" divi="teksti2" />
        <br /><br />
        <Teksti1 teksti={this.state.num1} divi="numero1" />
        <Teksti1 teksti={this.state.num2} divi="numero2" />
        <br /><br /><br /><br />
        <Teksti1 teksti="Oma Numero" divi="teksti2" />
        <br /><br />
        <Teksti1 teksti="1" divi="seuraava" />
        <br /><br />
        <button className="omanumero" >Ota Numero</button>
        <br /><br />
        <button className="peruuta" >Peruuta numero</button>
        <div><h2>{this.state.date.toLocaleTimeString()}</h2></div>
        <Random plus={this.state.plus} timer1={this.state.timer1}/>
        <Random plus2={this.state.plus} timer1={this.state.timer2}/>
      </div>
    )
  }
}

export default Jono
