import React from 'react';
import { Map as LeafletMap, Marker, Popup, TileLayer } from 'react-leaflet';
import {GeoJSON} from 'react-leaflet';
import axios from 'axios';
import { statements } from '@babel/template';
//import L from 'leaflet';
import ReactDOM from 'react-dom';
import Ajax from 'react-ajax';

class Map extends React.Component
{
  constructor()
  {
    super();

    this.state={

  /*lat: 37.81893781173967,
      lng: -122.47867584228514,
      zoom: 13,
      open: false,*/

      /*omadata: [
        {
          "type": "FeatureCollection",
          "features": [
            {
              "type": "Feature",
              "properties": {
                "FID": 2,
                "NIMI": "Elielinaukio",
                "URL": "<a href ='https://www.google.com/'>Google</a>"
              },
              "geometry": {
                "type": "Point",
                "coordinates": [
                  24.840087890624996,
                  60.75915950226991
                ]
              }
            }
            ,
            {
              "type": "Feature",
              "properties": {
                "FID": 3,
                "NIMI": "Rautatientori",
                "URL": "<a href ='https://www.google.com/'>Google</a>"
              },
              "geometry": {
                "type": "Point",
                "coordinates": [
                  25.6201171875,
                  60.89304469510537
                ]
              }
            }
          ]
        }
      ]*/

      omadata: "sdfdf"
    };

    //kokeilu
    //this.renderRecipes=this.renderRecipes.bind(this);
  }

  /*componentWillMount()
  {
    axios.get('https://gist.githubusercontent.com/NL-0/b6f17f8f86598eab84087db42c78f83a/raw/9699816ee7b9336e383d24082c40456bceaf6ff9/hel4.geojson')
    .then(response => {this.setState({response});
    });
  }*/

  //Collections?
  componentDidMount3()
  {
    axios.get('https://gist.githubusercontent.com/NL-0/b6f17f8f86598eab84087db42c78f83a/raw/9699816ee7b9336e383d24082c40456bceaf6ff9/hel4.geojson')
      .then((response) =>
    {
        const omadata=response.data;
        //this.setState({omadata: [...this.state.omadata]});
        //this.setState({omadata});
        this.setState({omadata:response.recipes});
        console.log(omadata);
    })
  }

  componentDidMount5()
  {
    Ajax.getJSON('https://gist.githubusercontent.com/NL-0/b6f17f8f86598eab84087db42c78f83a/raw/9699816ee7b9336e383d24082c40456bceaf6ff9/hel4.geojson')
      .then(
        response => {
          this.setState({
            extHTML: response
          });
        }, err => {
          alert("error ajaxissa:" + err);
        }
      );
  }

  componentDidMount4()
  {
    axios.get('https://gist.githubusercontent.com/NL-0/b6f17f8f86598eab84087db42c78f83a/raw/9699816ee7b9336e383d24082c40456bceaf6ff9/hel4.geojson')
      .then(({response}) =>
    {
        //this.setState({omadata:response.recipes});    //aiheuttaa errorin

        this.setState({omadata:response});
        console.log(response);

        //this.omadata=response;  //ei toimi
        /*this.setState((response)=>(
        {
          omadata: response
        }));*/

        //this.setState({omadata:[...response]});   //erroria

        //const omadata=response;
        //this.setState([...omadata]);
    })
    .catch((err)=>{alert("error: " + err);})
  }

  //https://techiediaries.com/react-ajax
  componentDidMount()
  {
    fetch('https://gist.githubusercontent.com/NL-0/b6f17f8f86598eab84087db42c78f83a/raw/9699816ee7b9336e383d24082c40456bceaf6ff9/hel4.geojson')
    //fetch('hel4.geojson')
      .then(response => response.json())
      .then((jsonData) => {

        this.setState({omadata:JSON.stringify(jsonData)});

        console.log(this.state.omadata);
        alert("omadata: " + this.state.omadata);
      })
      .catch((error) =>
      {
        alert("error jsondata: " + error);
      })
  }

  componentDidMount6()
  {
    fetch('https://gist.githubusercontent.com/NL-0/b6f17f8f86598eab84087db42c78f83a/raw/9699816ee7b9336e383d24082c40456bceaf6ff9/hel4.geojson')
    //fetch('hel4.geojson')
      .then(function(response)
      {
        return response.json();
      })
      .then(function(myJson)
      {
        console.log(JSON.stringify(myJson));
        this.setState({omadata:JSON.stringify(myJson)});
      });
/*   
      .catch((error) =>
      {
        alert("error jsondata: " + error);
      })*/
  }

  //https://www.reddit.com/r/reactjs/comments/a6nwqp/how_to_add_an_object_to_a_2d_array
  generateArray=(e)=>
  {
    let newarray=[]
    this.setState(
      {markers: [...this.state.markers,newarray]})
  }


  componentDidMount2()
  {
    fetch('https://gist.githubusercontent.com/NL-0/b6f17f8f86598eab84087db42c78f83a/raw/9699816ee7b9336e383d24082c40456bceaf6ff9/hel4.geojson')
    .then(response => response.json())
    .then(omadata => this.setState({omadata: [...this.state.omadata]}));
    //console.log(omadata);
  }

  render()
  {
    const position = [this.state.lat, this.state.lng];

    return (
      <LeafletMap
      center={[50, 10]}
      zoom={6}
      maxZoom={10}
      attributionControl={true}
      zoomControl={true}
      doubleClickZoom={true}
      scrollWheelZoom={true}
      dragging={true}
      //animate=import GeoJson from 'react';{true}
      easeLinearity={0.35}
    >
      <TileLayer
        url='http://{s}.tile.osm.org/{z}/{x}/{y}.png'

      />
      <Marker position={[50, 10]}>
        <Popup>
          Popup for any custom information.
        </Popup>
      </Marker>

      {/*<GeoJSON data={getGeoJson()} style={this.getStyle} onEachFeature={this.onEachFeature}/>*/}
         <GeoJSON data={this.state.omadata} style={this.getStyle} onEachFeature={this.onEachFeature}/>

    </LeafletMap>
  );   
  }

  onEachFeature = (feature, layer) =>
  {
    layer.on('click', function(e)
    {
      var omalayer=e.target;
      //alert('Clicked: ' + omalayer.feature.properties.name);
      omalayer.bindPopup(omalayer.feature.properties.NIMI + " "
        + omalayer.feature.properties.URL);
    });
  }

  getStyle(feature, layer) {
    return {
      color: '#006400',
      weight: 5,
      opacity: 0.65
    }
  }
}

export default Map
